import useAuth from '../../hooks/useAuth'

const Signin = () => {
    const { signin } = useAuth()

    const [ email, setEmail ] = useState("");
    const [ senha, setSenha ] = useState("");
    const [ error, setError ] = useState("");

    return(
        <C.Container>
            <C.Label>SISTEMA DE LOGIN</C.Label>
